#!/bin/bash
set -e
set -x

# Generate python proto artifacts
python -m grpc_tools.protoc -I../proto --python_out=src/momento_wire_types --grpc_python_out=src/momento_wire_types cacheclient.proto controlclient.proto

# Protobuf imports generated by Google are broken and need manual fixing
# https://github.com/protocolbuffers/protobuf/issues/1491
sed -i '.old' 's/^import cacheclient_pb2 as cacheclient__pb2$/from . import cacheclient_pb2 as cacheclient__pb2/' src/momento_wire_types/cacheclient_pb2_grpc.py
sed -i '.old' 's/^import controlclient_pb2 as controlclient__pb2$/from . import controlclient_pb2 as controlclient__pb2/' src/momento_wire_types/controlclient_pb2_grpc.py

python -m build